mapper.select:
SELECT f.id, f.user_id, f.resource_type, f.resource_id, f.create_time, r.resource_name, r.description, r.cover_image, r.view_count, r.download_count, CASE f.resource_type WHEN 'SIMULATION' THEN '虚拟仿真' WHEN 'VIDEO' THEN '视频' WHEN 'AUDIO' THEN '音频' WHEN 'DOCUMENT' THEN '文档' ELSE '其他' END AS resource_type_name FROM t_user_favorite f LEFT JOIN t_resource r ON f.resource_id = r.id AND f.resource_type = 'RESOURCE' WHERE f.user_id = #{userId}
AND f.resource_type = #{resourceType}
ORDER BY f.create_time DESC

SELECT id, user_id, resource_type, resource_id, create_time
FROM t_user_favorite
WHERE user_id = #{userId}
  AND resource_type = #{resourceType}
  AND resource_id = #{resourceId}

SELECT COUNT(*)
FROM t_user_favorite
WHERE user_id = #{userId}

    AND resource_type = #{resourceType}
